select b.board_num,b.board_subject,b.board_cont,b.board_date,b.board_updateDate,b.board_views,b.board_like,b.board_scope,b.board_blind,b.mem_id,b.board_thumFileName
	,m.menu_num,m.menu_name,m.menu_blind,m.main_num,m.mid_num 
		from board b left outer join menu m on b.menu_num = m.menu_num where b.mem_id =#{param3} order by  b.board_num desc limit #{param1} offset #{param2}

select b.board_num,b.board_subject,b.board_cont,b.board_date,b.board_updateDate,b.board_views,b.board_like,b.board_scope,b.board_blind,b.mem_id,b.board_thumFileName
	,m.menu_num,m.menu_name,m.menu_blind,m.main_num,m.mid_num 
		from board b left outer join menu m on b.menu_num = m.menu_num where b.mem_id =#{param3} and m.menu_num=#{param4} order by  b.board_num desc limit #{param1} offset #{param2}
